import React, {useEffect, useState} from 'react';
import {Navbar, Nav, Container, Col, Image, Accordion, Button} from 'react-bootstrap';
import ProjectModal from "./ProjectModal";
import axios from 'axios';
import moment from 'moment';

const Navigation = ({accessToken}) => {
  const [logged, setLogged] = useState(true);
  const [isAdmin, setAdmin] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [projects, setProjects] = useState([]);

  // Fetch projects from the API on component load
  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('/api/projects', {
      headers: {
        Authorization: "Bearer " + accessToken,
      },
    });

      if (response.status === 200) {
        const data = await response.data;

        data.forEach(data => {
          data.creation_date = moment(data.creation_date).format('YYYY-MM-DD');
          data.deadline = moment(data.deadline).format('YYYY-MM-DD');
        });

        setProjects(data);
      } else {
        console.error('Error fetching projects:', response.status);
      }
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  return (
    <Navbar bg="dark" variant="dark">
      <Container>
        <Navbar.Brand>
            <Nav.Link href="/"><Image src="logo.png" width="50" rounded /></Nav.Link>
        ProjectUJ
        </Navbar.Brand>
        <Nav>
            <Accordion>
            <Accordion.Item eventKey="0">
             <Accordion.Header as={Nav.Link} >My Projects</Accordion.Header>
                <Accordion.Body>
                    {projects.map((project) => (
                        <Nav.Link href={`/projects/${project.project_id}`}>{project.project_name}</Nav.Link>
                      ))}
                </Accordion.Body>
              </Accordion.Item >
          </Accordion>
            {logged && <Nav.Link href="/projects">Projects</Nav.Link>}
            {logged && <Nav.Link href="/tasks">Tasks</Nav.Link>}
            {logged && isAdmin && <Nav.Link href="/users">Users</Nav.Link>}
            {logged && <Nav.Link href="/logout">Logout</Nav.Link>}
        </Nav>
      </Container>
    </Navbar>
  );
};

export default Navigation;
