import React, { useState, useEffect } from 'react';
import { ListGroup, Spinner } from 'react-bootstrap';

const ContinuousScrolling = () => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [page, setPage] = useState(1);

  const fetchData = async () => {
    setIsLoading(true);
    // Simulating data fetching from the server
    const response = await fetch(`https://www.reddit.com/r/Wallstreetbets/top.json?limit=10&t=year}`);
    const newData = await response.json();
    setData((prevData) => [...prevData, ...newData]);
    setPage((prevPage) => prevPage + 1);
    setIsLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleScroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop ===
      document.documentElement.offsetHeight
    ) {
      fetchData();
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div>
      <h2>Continuous Scrolling Example</h2>
      <ListGroup>
        {data.map((item) => (
          <ListGroup.Item key={item.id}>
            <strong>{item.title}</strong> - {item.description}
          </ListGroup.Item>
        ))}
      </ListGroup>
      {isLoading && (
        <div className="text-center mt-3">
          <Spinner animation="border" />
        </div>
      )}
    </div>
  );
};

export default ContinuousScrolling;
