import React, {useEffect, useState} from 'react';
import {Navbar, Nav, Container, NavDropdown , Image, Accordion} from 'react-bootstrap';
import ProjectModal from "./ProjectModal";
import axios from 'axios';
import moment from 'moment';

const Navigation = ({accessToken}) => {
  const [logged, setLogged] = useState(true);
  const [isAdmin, setAdmin] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [projects, setProjects] = useState([]);

  // Fetch projects from the API on component load
  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('/api/projects', {
      headers: {
        Authorization: "Bearer " + accessToken,
      },
    });

      if (response.status === 200) {
        const data = await response.data;

        data.forEach(data => {
          data.creation_date = moment(data.creation_date).format('YYYY-MM-DD');
          data.deadline = moment(data.deadline).format('YYYY-MM-DD');
        });

        setProjects(data);
      } else {
        console.error('Error fetching projects:', response.status);
      }
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const handleOpenModal = (project) => {
    setSelectedProject(project);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <Navbar bg="dark" variant="dark">
      <Container>
        <Navbar.Brand>
            <Nav.Link href="/"><Image src="logo.png" width="50" rounded /></Nav.Link>
        ProjectUJ
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-dark-example" />
        <Nav>
          <NavDropdown
              id="nav-dropdown-dark-example"
              title="My Projects"
              menuVariant="dark"
            >
              {projects.map((project) => (
                  <NavDropdown.Item onClick={() => handleOpenModal(project)}>{project.project_name}</NavDropdown.Item>
              ))}
            </NavDropdown>
            {logged && <Nav.Link href="/projects">Projects</Nav.Link>}
            {logged && <Nav.Link href="/tasks">Tasks</Nav.Link>}
            {logged && isAdmin && <Nav.Link href="/users">Users</Nav.Link>}
            {logged && <Nav.Link href="/logout">Logout</Nav.Link>}
        </Nav>
        {selectedProject && (
          <ProjectModal project={selectedProject} show={showModal} handleClose={handleCloseModal} />
        )}
      </Container>
    </Navbar>
  );
};

export default Navigation;
