import React, { useState } from 'react';
import { Table, Form, Container, Button } from 'react-bootstrap';

const UsersPage = () => {
  const [filter, setFilter] = useState('');
  const [sortBy, setSortBy] = useState('name');

  const users = [
    { user_id: 1, first_name: 'John', last_name: 'Doe', email: 'john.doe@example.com', role: 'user' },
    { user_id: 2, first_name: 'Jane', last_name: 'Smith', email: 'jane.smith@example.com', role: 'admin' },
    { user_id: 3, first_name: 'Bob', last_name: 'Johnson', email: 'bob.johnson@example.com', role: 'user' },
  ];

  const filteredUsers = users.filter((user) =>
    user.first_name.toLowerCase().includes(filter.toLowerCase()) ||
    user.last_name.toLowerCase().includes(filter.toLowerCase()) ||
    user.email.toLowerCase().includes(filter.toLowerCase()) ||
    user.role.toLowerCase().includes(filter.toLowerCase())
  );

  const sortedUsers = filteredUsers.sort((a, b) => {
    if (sortBy === 'name') {
      return a.first_name.localeCompare(b.first_name);
    } else if (sortBy === 'email') {
      return a.email.localeCompare(b.email);
    } else if (sortBy === 'role') {
      return a.role.localeCompare(b.role);
    }
    return 0;
  });

  const handleOpenUser = (userId) => {
    // Implement your logic to open the user record based on the userId
    console.log(`Opening user record with ID: ${userId}`);
  };

  return (
    <Container className="text-center mt-4">
      <h2>Users List</h2>
      <div className="d-flex justify-content-between mb-2">
        <Form.Group controlId="sortBy">
          <Form.Control
            as="select"
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="name">Name</option>
            <option value="email">Email</option>
            <option value="role">Role</option>
          </Form.Control>
        </Form.Group>
      </div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>
              <Form.Control
                type="text"
                placeholder="Filter by Name"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
              />
            </th>
            <th>
              <Form.Control
                type="text"
                placeholder="Filter by Email"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
              />
            </th>
            <th>
              <Form.Control
                type="text"
                placeholder="Filter by Role"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
              />
            </th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {sortedUsers.map((user) => (
            <tr key={user.user_id}>
              <td>{user.first_name} {user.last_name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <Button variant="primary" onClick={() => handleOpenUser(user.user_id)}>
                  Open Record
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default UsersPage;
